  and payoff_date = #{payoffDate,jdbcType=VARCHAR}
  and payoff_type_code = #{payoffTypeCode,jdbcType=VARCHAR}
  and payoff_type_sub_number_code = #{payoffTypeSubNumberCode,jdbcType=VARCHAR}
  and cash_register_no = #{cashRegisterNo,jdbcType=NUMERIC}
  and history_type = #{historyType,jdbcType=NUMERIC}
  and correction_history_sub_number = #{correctionHistorySubNumber,jdbcType=NUMERIC}

mapper.update:

update t_alteration_history_pay_off_data


    payoff_type_name = #{payoffTypeName,jdbcType=VARCHAR},


    payoff_type_sub_number_name = #{payoffTypeSubNumberName,jdbcType=VARCHAR},


