mapper.select:

select 

from t_alteration_history_sales_transaction_tender
where transaction_id = #{transactionId,jdbcType=VARCHAR}
  and order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and tender_group = #{tenderGroup,jdbcType=VARCHAR}
  and tender_id = #{tenderId,jdbcType=VARCHAR}
  and history_type = #{historyType,jdbcType=NUMERIC}
  and tender_sub_number = #{tenderSubNumber,jdbcType=NUMERIC}

mapper.delete:

delete from t_alteration_history_sales_transaction_tender
where transaction_id = #{transactionId,jdbcType=VARCHAR}
  and order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and tender_group = #{tenderGroup,jdbcType=VARCHAR}
  and tender_id = #{tenderId,jdbcType=VARCHAR}
  and history_type = #{historyType,jdbcType=NUMERIC}
  and tender_sub_number = #{tenderSubNumber,jdbcType=NUMERIC}

mapper.update:

update t_alteration_history_sales_transaction_tender


    sales_transaction_error_id = #{salesTransactionErrorId,jdbcType=VARCHAR},


    payment_sign = #{paymentSign,jdbcType=VARCHAR},


    tax_included_payment_amount_currency_code = #{taxIncludedPaymentAmountCurrencyCode,jdbcType=VARCHAR},


    tax_included_payment_amount_value = #{taxIncludedPaymentAmountValue,jdbcType=NUMERIC},


    create_user_id = #{createUserId,jdbcType=VARCHAR},


    create_datetime = #{createDatetime,jdbcType=TIMESTAMP},


    create_program_id = #{createProgramId,jdbcType=VARCHAR},


    update_user_id = #{updateUserId,jdbcType=VARCHAR},


    update_datetime = #{updateDatetime,jdbcType=TIMESTAMP},


    update_program_id = #{updateProgramId,jdbcType=VARCHAR},


where transaction_id = #{transactionId,jdbcType=VARCHAR}
  and order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and tender_group = #{tenderGroup,jdbcType=VARCHAR}
  and tender_id = #{tenderId,jdbcType=VARCHAR}
  and history_type = #{historyType,jdbcType=NUMERIC}
  and tender_sub_number = #{tenderSubNumber,jdbcType=NUMERIC}


update t_alteration_history_sales_transaction_tender
set sales_transaction_error_id = #{salesTransactionErrorId,jdbcType=VARCHAR},
  payment_sign = #{paymentSign,jdbcType=VARCHAR},
  tax_included_payment_amount_currency_code = #{taxIncludedPaymentAmountCurrencyCode,jdbcType=VARCHAR},
  tax_included_payment_amount_value = #{taxIncludedPaymentAmountValue,jdbcType=NUMERIC},
  create_user_id = #{createUserId,jdbcType=VARCHAR},
  create_datetime = #{createDatetime,jdbcType=TIMESTAMP},
  create_program_id = #{createProgramId,jdbcType=VARCHAR},
  update_user_id = #{updateUserId,jdbcType=VARCHAR},
  update_datetime = #{updateDatetime,jdbcType=TIMESTAMP},
  update_program_id = #{updateProgramId,jdbcType=VARCHAR}
where transaction_id = #{transactionId,jdbcType=VARCHAR}
  and order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and tender_group = #{tenderGroup,jdbcType=VARCHAR}
  and tender_id = #{tenderId,jdbcType=VARCHAR}
  and history_type = #{historyType,jdbcType=NUMERIC}
  and tender_sub_number = #{tenderSubNumber,jdbcType=NUMERIC}
