mapper.select:

select 

from t_alteration_exclusion_control
where store_code = #{storeCode,jdbcType=VARCHAR}
  and business_date = #{businessDate,jdbcType=VARCHAR}
  and cash_register_no = #{cashRegisterNo,jdbcType=NUMERIC}
  and data_alteration_type = #{dataAlterationType,jdbcType=VARCHAR}

mapper.delete:

delete from t_alteration_exclusion_control
where store_code = #{storeCode,jdbcType=VARCHAR}
  and business_date = #{businessDate,jdbcType=VARCHAR}
  and cash_register_no = #{cashRegisterNo,jdbcType=NUMERIC}
  and data_alteration_type = #{dataAlterationType,jdbcType=VARCHAR}

mapper.update:

update t_alteration_exclusion_control


    exclusion_valid_time = #{exclusionValidTime,jdbcType=VARCHAR},


    create_user_id = #{createUserId,jdbcType=VARCHAR},


    create_datetime = #{createDatetime,jdbcType=TIMESTAMP},


    create_program_id = #{createProgramId,jdbcType=VARCHAR},


    update_user_id = #{updateUserId,jdbcType=VARCHAR},


    update_datetime = #{updateDatetime,jdbcType=TIMESTAMP},


    update_program_id = #{updateProgramId,jdbcType=VARCHAR},


where store_code = #{storeCode,jdbcType=VARCHAR}
  and business_date = #{businessDate,jdbcType=VARCHAR}
  and cash_register_no = #{cashRegisterNo,jdbcType=NUMERIC}
  and data_alteration_type = #{dataAlterationType,jdbcType=VARCHAR}


update t_alteration_exclusion_control
set exclusion_valid_time = #{exclusionValidTime,jdbcType=VARCHAR},
  create_user_id = #{createUserId,jdbcType=VARCHAR},
  create_datetime = #{createDatetime,jdbcType=TIMESTAMP},
  create_program_id = #{createProgramId,jdbcType=VARCHAR},
  update_user_id = #{updateUserId,jdbcType=VARCHAR},
  update_datetime = #{updateDatetime,jdbcType=TIMESTAMP},
  update_program_id = #{updateProgramId,jdbcType=VARCHAR}
where store_code = #{storeCode,jdbcType=VARCHAR}
  and business_date = #{businessDate,jdbcType=VARCHAR}
  and cash_register_no = #{cashRegisterNo,jdbcType=NUMERIC}
  and data_alteration_type = #{dataAlterationType,jdbcType=VARCHAR}
