mapper.select:

select 

from m_color_size
where system_brand_code = #{systemBrandCode,jdbcType=VARCHAR}
  and system_country_code = #{systemCountryCode,jdbcType=VARCHAR}
  and difference_type_code = #{differenceTypeCode,jdbcType=VARCHAR}
  and difference_id = #{differenceId,jdbcType=VARCHAR}

mapper.delete:

delete from m_color_size
where system_brand_code = #{systemBrandCode,jdbcType=VARCHAR}
  and system_country_code = #{systemCountryCode,jdbcType=VARCHAR}
  and difference_type_code = #{differenceTypeCode,jdbcType=VARCHAR}
  and difference_id = #{differenceId,jdbcType=VARCHAR}

mapper.update:

update m_color_size
set system_business_code = #{systemBusinessCode,jdbcType=VARCHAR},
  difference_name = #{differenceName,jdbcType=VARCHAR},
  view_difference_code = #{viewDifferenceCode,jdbcType=VARCHAR},
  create_user_id = #{createUserId,jdbcType=VARCHAR},
  create_datetime = #{createDatetime,jdbcType=TIMESTAMP},
  create_program_id = #{createProgramId,jdbcType=VARCHAR},
  update_user_id = #{updateUserId,jdbcType=VARCHAR},
  update_datetime = #{updateDatetime,jdbcType=TIMESTAMP},
  update_program_id = #{updateProgramId,jdbcType=VARCHAR}
where system_brand_code = #{systemBrandCode,jdbcType=VARCHAR}
  and system_country_code = #{systemCountryCode,jdbcType=VARCHAR}
  and difference_type_code = #{differenceTypeCode,jdbcType=VARCHAR}
  and difference_id = #{differenceId,jdbcType=VARCHAR}
