mapper.select:

select 

from m_item_level_2
where system_brand_code = #{systemBrandCode,jdbcType=VARCHAR}
  and system_country_code = #{systemCountryCode,jdbcType=VARCHAR}
  and item_code = #{itemCode,jdbcType=VARCHAR}

mapper.delete:

delete from m_item_level_2
where system_brand_code = #{systemBrandCode,jdbcType=VARCHAR}
  and system_country_code = #{systemCountryCode,jdbcType=VARCHAR}
  and item_code = #{itemCode,jdbcType=VARCHAR}
