mapper.select:

select 

from m_item_list_head
where system_brand_code = #{systemBrandCode,jdbcType=VARCHAR}
  and system_country_code = #{systemCountryCode,jdbcType=VARCHAR}
  and skulist_code = #{skulistCode,jdbcType=VARCHAR}

mapper.delete:

delete from m_item_list_head
where system_brand_code = #{systemBrandCode,jdbcType=VARCHAR}
  and system_country_code = #{systemCountryCode,jdbcType=VARCHAR}
  and skulist_code = #{skulistCode,jdbcType=VARCHAR}
