mapper.select:

select 

from t_lot_number_mgm
where lot_number = #{lotNumber,jdbcType=VARCHAR}
  and out_bound_id = #{outBoundId,jdbcType=VARCHAR}
  and table_id = #{tableId,jdbcType=VARCHAR}

mapper.delete:

delete from t_lot_number_mgm
where lot_number = #{lotNumber,jdbcType=VARCHAR}
  and out_bound_id = #{outBoundId,jdbcType=VARCHAR}
  and table_id = #{tableId,jdbcType=VARCHAR}

mapper.update:

update t_lot_number_mgm
set store_group_code = #{storeGroupCode,jdbcType=VARCHAR},
  batch_region = #{batchRegion,jdbcType=NUMERIC},
  check_flag = #{checkFlag,jdbcType=BIT},
  create_user_id = #{createUserId,jdbcType=VARCHAR},
  create_datetime = #{createDatetime,jdbcType=TIMESTAMP},
  create_program_id = #{createProgramId,jdbcType=VARCHAR},
  update_user_id = #{updateUserId,jdbcType=VARCHAR},
  update_datetime = #{updateDatetime,jdbcType=TIMESTAMP},
  update_program_id = #{updateProgramId,jdbcType=VARCHAR}
where lot_number = #{lotNumber,jdbcType=VARCHAR}
  and out_bound_id = #{outBoundId,jdbcType=VARCHAR}
  and table_id = #{tableId,jdbcType=VARCHAR}
