mapper.select:

select 

from t_store_hourly_sku_result
where store_code = #{storeCode,jdbcType=VARCHAR}
  and business_date = #{businessDate,jdbcType=VARCHAR}
  and skulist_code = #{skulistCode,jdbcType=VARCHAR}
  and time_local = #{timeLocal,jdbcType=VARCHAR}

mapper.delete:

delete from t_store_hourly_sku_result
where store_code = #{storeCode,jdbcType=VARCHAR}
  and business_date = #{businessDate,jdbcType=VARCHAR}
  and skulist_code = #{skulistCode,jdbcType=VARCHAR}
  and time_local = #{timeLocal,jdbcType=VARCHAR}

mapper.update:

update t_store_hourly_sku_result
set transaction_create_date = #{transactionCreateDate,jdbcType=VARCHAR},
  time_utc = #{timeUtc,jdbcType=VARCHAR},
  sales_hour = #{salesHour,jdbcType=NUMERIC},
  sales_quantity_code = #{salesQuantityCode,jdbcType=VARCHAR},
  sales_quantity = #{salesQuantity,jdbcType=NUMERIC},
  create_user_id = #{createUserId,jdbcType=VARCHAR},
  create_datetime = #{createDatetime,jdbcType=TIMESTAMP},
  create_program_id = #{createProgramId,jdbcType=VARCHAR},
  update_user_id = #{updateUserId,jdbcType=VARCHAR},
  update_datetime = #{updateDatetime,jdbcType=TIMESTAMP},
  update_program_id = #{updateProgramId,jdbcType=VARCHAR}
where store_code = #{storeCode,jdbcType=VARCHAR}
  and business_date = #{businessDate,jdbcType=VARCHAR}
  and skulist_code = #{skulistCode,jdbcType=VARCHAR}
  and time_local = #{timeLocal,jdbcType=VARCHAR}
