mapper.select:

select 

from t_transaction_inquiry_sales_transaction_discount
where order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and detail_sub_number = #{detailSubNumber,jdbcType=NUMERIC}
  and promotion_type = #{promotionType,jdbcType=VARCHAR}
  and promotion_no = #{promotionNo,jdbcType=VARCHAR}
  and store_discount_type = #{storeDiscountType,jdbcType=VARCHAR}

