mapper.select:

select 

from t_transaction_inquiry_sales_transaction_tax
where order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and detail_sub_number = #{detailSubNumber,jdbcType=NUMERIC}
  and tax_group = #{taxGroup,jdbcType=VARCHAR}
  and tax_sub_number = #{taxSubNumber,jdbcType=NUMERIC}

mapper.delete:

delete from t_transaction_inquiry_sales_transaction_tax
where order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and detail_sub_number = #{detailSubNumber,jdbcType=NUMERIC}
  and tax_group = #{taxGroup,jdbcType=VARCHAR}
  and tax_sub_number = #{taxSubNumber,jdbcType=NUMERIC}

mapper.update:

update t_transaction_inquiry_sales_transaction_tax
set tax_name = #{taxName,jdbcType=VARCHAR},
  tax_amount_sign = #{taxAmountSign,jdbcType=VARCHAR},
  tax_amount_currency_code = #{taxAmountCurrencyCode,jdbcType=VARCHAR},
  tax_amount_value = #{taxAmountValue,jdbcType=NUMERIC},
  tax_rate = #{taxRate,jdbcType=NUMERIC},
  create_user_id = #{createUserId,jdbcType=VARCHAR},
  create_datetime = #{createDatetime,jdbcType=TIMESTAMP},
  create_program_id = #{createProgramId,jdbcType=VARCHAR},
  update_user_id = #{updateUserId,jdbcType=VARCHAR},
  update_datetime = #{updateDatetime,jdbcType=TIMESTAMP},
  update_program_id = #{updateProgramId,jdbcType=VARCHAR}
where order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and detail_sub_number = #{detailSubNumber,jdbcType=NUMERIC}
  and tax_group = #{taxGroup,jdbcType=VARCHAR}
  and tax_sub_number = #{taxSubNumber,jdbcType=NUMERIC}
