mapper.select:

select 

from t_transaction_inquiry_sales_transaction_tender
where order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and tender_group = #{tenderGroup,jdbcType=VARCHAR}
  and tender_id = #{tenderId,jdbcType=VARCHAR}
  and tender_sub_number = #{tenderSubNumber,jdbcType=NUMERIC}

mapper.delete:

delete from t_transaction_inquiry_sales_transaction_tender
where order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and tender_group = #{tenderGroup,jdbcType=VARCHAR}
  and tender_id = #{tenderId,jdbcType=VARCHAR}
  and tender_sub_number = #{tenderSubNumber,jdbcType=NUMERIC}

mapper.update:

