  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and tender_group = #{tenderGroup,jdbcType=VARCHAR}
  and tender_id = #{tenderId,jdbcType=VARCHAR}
  and tender_sub_number = #{tenderSubNumber,jdbcType=NUMERIC}


