mapper.select:

select 

from t_transaction_inquiry_sales_transaction_total_amount
where order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and total_type = #{totalType,jdbcType=VARCHAR}

mapper.delete:

delete from t_transaction_inquiry_sales_transaction_total_amount
where order_sub_number = #{orderSubNumber,jdbcType=NUMERIC}
  and sales_transaction_id = #{salesTransactionId,jdbcType=VARCHAR}
  and total_type = #{totalType,jdbcType=VARCHAR}
