mapper.select:

select 

from w_non_item
where lot_num = #{lotNum,jdbcType=VARCHAR}
  and system_country_code = #{systemCountryCode,jdbcType=VARCHAR}
  and system_brand_code = #{systemBrandCode,jdbcType=VARCHAR}
  and non_item_code = #{nonItemCode,jdbcType=VARCHAR}
  and apply_start_date = #{applyStartDate,jdbcType=TIMESTAMP}

mapper.delete:

delete from w_non_item
where lot_num = #{lotNum,jdbcType=VARCHAR}
  and system_country_code = #{systemCountryCode,jdbcType=VARCHAR}
  and system_brand_code = #{systemBrandCode,jdbcType=VARCHAR}
  and non_item_code = #{nonItemCode,jdbcType=VARCHAR}
  and apply_start_date = #{applyStartDate,jdbcType=TIMESTAMP}
