mapper.select:

select 

from w_skulist_child
where lot_number = #{lotNumber,jdbcType=VARCHAR}
  and business_code = #{businessCode,jdbcType=NUMERIC}
  and country_code = #{countryCode,jdbcType=VARCHAR}
  and skulist_code = #{skulistCode,jdbcType=NUMERIC}
  and item_code = #{itemCode,jdbcType=VARCHAR}
  and eai_update_date = #{eaiUpdateDate,jdbcType=VARCHAR}

mapper.delete:

delete from w_skulist_child
where lot_number = #{lotNumber,jdbcType=VARCHAR}
  and business_code = #{businessCode,jdbcType=NUMERIC}
  and country_code = #{countryCode,jdbcType=VARCHAR}
  and skulist_code = #{skulistCode,jdbcType=NUMERIC}
  and item_code = #{itemCode,jdbcType=VARCHAR}
  and eai_update_date = #{eaiUpdateDate,jdbcType=VARCHAR}

mapper.update:

update w_skulist_child
set eai_update_type = #{eaiUpdateType,jdbcType=VARCHAR},
  eai_send_status = #{eaiSendStatus,jdbcType=VARCHAR},
  eai_send_date = #{eaiSendDate,jdbcType=VARCHAR},
  item_level = #{itemLevel,jdbcType=NUMERIC},
  inventory_management_level = #{inventoryManagementLevel,jdbcType=NUMERIC},
  pack_ind = #{packInd,jdbcType=VARCHAR},
  insert_id = #{insertId,jdbcType=VARCHAR},
  insert_date = #{insertDate,jdbcType=TIMESTAMP},
  create_datetime = #{createDatetime,jdbcType=TIMESTAMP},
  last_update_datetime = #{lastUpdateDatetime,jdbcType=TIMESTAMP},
  last_update_id = #{lastUpdateId,jdbcType=VARCHAR},
  main_item_type = #{mainItemType,jdbcType=VARCHAR},
  inner_pack_size = #{innerPackSize,jdbcType=NUMERIC},
  logical_delete_type = #{logicalDeleteType,jdbcType=VARCHAR},
  batch_region = #{batchRegion,jdbcType=NUMERIC}
where lot_number = #{lotNumber,jdbcType=VARCHAR}
  and business_code = #{businessCode,jdbcType=NUMERIC}
  and country_code = #{countryCode,jdbcType=VARCHAR}
  and skulist_code = #{skulistCode,jdbcType=NUMERIC}
  and item_code = #{itemCode,jdbcType=VARCHAR}
  and eai_update_date = #{eaiUpdateDate,jdbcType=VARCHAR}
