mapper.select:

select 

from w_store
where lot_number = #{lotNumber,jdbcType=VARCHAR}
  and store_code = #{storeCode,jdbcType=NUMERIC}
  and eai_update_date = #{eaiUpdateDate,jdbcType=VARCHAR}

mapper.delete:

delete from w_store
where lot_number = #{lotNumber,jdbcType=VARCHAR}
  and store_code = #{storeCode,jdbcType=NUMERIC}
  and eai_update_date = #{eaiUpdateDate,jdbcType=VARCHAR}
