mapper.select:

select 

from w_tender
where lot_number = #{lotNumber,jdbcType=VARCHAR}
  and store_code = #{storeCode,jdbcType=VARCHAR}
  and tender_id = #{tenderId,jdbcType=NUMERIC}
  and eai_update_datetime = #{eaiUpdateDatetime,jdbcType=VARCHAR}

mapper.delete:

delete from w_tender
where lot_number = #{lotNumber,jdbcType=VARCHAR}
  and store_code = #{storeCode,jdbcType=VARCHAR}
  and tender_id = #{tenderId,jdbcType=NUMERIC}
  and eai_update_datetime = #{eaiUpdateDatetime,jdbcType=VARCHAR}
